import './App.css';
import React from 'react'
import ProgressBar from "@ramonak/react-progress-bar";

function App() {
  const [start, setStart] = React.useState('20:30');  
  const [end, setEnd] = React.useState('21:15');  
  const [display, setDisplay] = React.useState(true);
  const [time, setTime] = React.useState(123);
  const [percent, setPercent] = React.useState(123);

  function changePage()
  {
    setDisplay(!display)
  }

  return (  
    <div className="App">
      { display ? <div className="mainPage">
        <input placeholder='Начало урока' onChange={event => setStart(event.target.value)} value={start}></input>
        <input placeholder='Конец урока' onChange={event => setEnd(event.target.value)} value={end}></input>
        <button onClick={changePage}>GO</button>
      </div>
      : <div className="timePage">
        <h1>{time}</h1>
        <h3>{percent}</h3>
        <ProgressBar completed={80}/>
      </div>
      }
    </div>
  );
}

export default App;
