import './App.css';
import React from 'react'
function App() {
  const [start, setStart] = React.useState('20:30');  
  const [end, setEnd] = React.useState('21:15');  
  const [display, setDisplay] = React.useState(true);
  const [time, setTime] = React.useState(new Date());

  setInterval(tick, 1000);

  function tick()
  {
    setTime(new Date());
  }

  function click()
  {
    setDisplay(false);
    const arrayStart = start.split(":");
    const arrayEnd = end.split(":");
    let timeOnly = (new Date(0, 0, 0, arrayEnd[0], arrayEnd[1], 0) - new Date(0, 0, 0, arrayStart[0], arrayStart[1], 0)) / 60 / 1000
    let timePassed = (new Date(0, 0, 0, parseInt(time.getHours().toString(), 10), parseInt(time.getMinutes().toString(), 10), 0) - new Date(0, 0, 0, arrayStart[0], arrayStart[1], 0)) / 60 / 1000
    console.log(timeOnly);
    console.log(timePassed);
  }

  return (  
    <div className="App">
      { display ? <div>
        <input placeholder='Начало урока' onChange={event => setStart(event.target.value)} value={start}></input>
        <input placeholder='Конец урока' onChange={event => setEnd(event.target.value)} value={end}></input>
        <button onClick={click}>GO</button>
      </div>
      : <h1>{time.toLocaleTimeString()}</h1>
      }
    </div>
  );
}

export default App;
