import './App.css';
import React from 'react'
function App() {
  const [start, setStart] = React.useState('');  
  const [end, setEnd] = React.useState('');  
  const [display, setDisplay] = React.useState(true);
  const [time, setTime] = React.useState(new Date().toLocaleTimeString());

  setInterval(tick, 1000);

  function tick()
  {
    setTime(new Date().toLocaleTimeString());
  }

  function click()
  {
    setDisplay(false);
    const arrayStart = start.split(":");
    const arrayEnd = end.split(":");
    let timeLength = Date(arrayEnd[0], arrayEnd[1])
    console.log(timeLength.toString())
  }

  return (  
    <div className="App">
      { display ? <div>
        <input placeholder='Начало урока' onChange={event => setStart(event.target.value)} value={start}></input>
        <input placeholder='Конец урока' onChange={event => setEnd(event.target.value)} value={end}></input>
        <button onClick={click}>GO</button>
      </div>
      : <h1>{time}</h1>
      }
    </div>
  );
}

export default App;
